struct Cell
{
	bool LeftWall;
	bool RightWall;
	bool TopWall;
	bool BottomWall;
};

struct Board
{
	Cell cells[7][5];
};

task main()
{
	Board board;
	for (int i = 0; i < 7; i++)
	{
		for (int j = 0; i < 5; i++)
		{
			board.cells[i][j].LeftWall = false;
			board.cells[i][j].RightWall = false;
			board.cells[i][j].TopWall = false;
			board.cells[i][j].BottomWall = false;
			if(j == 0)
			{
				board.cells[i][j].TopWall = true;
			}
			if(j == 6)
			{
				board.cells[i][j].TopWall = true;
			}
			if(i == 0)
			{
				board.cells[i][j].LeftWall = true;
			}
			if(i == 4)
			{
				board.cells[i][j].BottomWall = true;
			}
		}
	}
}
